    <script>
        var soslRegex = /%3F|%26|%7C|%7B|%7D|%5B|%5D|%5E|%3A|%5C|%22|%2B|['!\(\)~\*-]/g;
        var textBoxId = "articleSearchBox";

        // different typeaheads need their own data
        var boxData;
        if (!boxData) boxData = {};

        boxData['articleSearchBox'] = {
            setSelectedValue :
                function( ctrl, id, value ) {

                    // put the id on the input tag
                    $(ctrl).attr('data-id', id);

                    // if destinations are defined, set them too
                    var destinationId = '';
                    if (destinationId) {
                        $('[id$=]').val( id );
                        $('[id$=]').val( value );
                    }
                },

            fieldList:
                ['Title', 'MasterVersionId', 'Language'],

            boxQuery:
                function( queryString, typeaheadCallback ) {
                    // track search request in google analytics
                    ga('send', 'event', 'helpcenter', 'search-hc', queryString);
                    Visualforce.remoting.Manager.invokeAction(
                        'SearchComponentCtrl.searchArticles',
                        queryString,
                        boxData['articleSearchBox'].fieldList,
                        'ALL',
                        'en_US',
                        'Title',
                        5,
                        function(result, event){
                            if (event.status) {
                                var dataList = [];
                                $.each(result, function(i, r) {
                                    if( i >= 5) return;
                                    dataList.push({
                                        id: r.ArticleNumber,
                                        value: r.Title +
                                            ((''.length && r['']) ?
                                            (' (' + r[''] + ')') : '')
                                    });
                                });
                                typeaheadCallback( dataList );
                            } else {
                              // TODO (gaziz_tazhenov) deal with Exception
                                typeaheadCallback( [] );
                            }
                        },
                        {escape: false}
                    );
                }
            };

        $(document).ready(function(){
            $('#articleSearchBox').typeahead({
                minLength: 3,
                hint: true,
                autoselect: true,
                highlight: true
            },
            {
                source: boxData['articleSearchBox'].boxQuery, // calls our remoting function
                name: 'articleSearchBoxTypeahead',
            }).on('typeahead:selected', function (object, datum) {
                boxData['articleSearchBox'].setSelectedValue( this, datum.id, datum.value );
            }).on('typeahead:autocompleted', function (object, datum) {
                boxData['articleSearchBox'].setSelectedValue( this, datum.id, datum.value );
            }).keyup( function(ev) {
                // if they press backspace or escape, we want to clear the selection
                if ( ev.which == 8 || ev.which == 27 ) {
                    boxData['articleSearchBox'].setSelectedValue( this, null, null );
                }
            });

            //if (true) $('#articleSearchBox').focus();
        });

        if (''.length)
            boxData['articleSearchBox'].fieldList.push('');

        if ('ArticleNumber'.length)
            boxData['articleSearchBox'].fieldList.push('ArticleNumber');


        function redirectToArticle(e) {
            if (e) {
                var code = (e.keyCode ? e.keyCode : e.which);
            }

            if(!e || code == 13) { //Enter keycode
                window.setTimeout(redirectToExactArticle, 200);
            }
        }

        function redirectToExactArticle(data) {
          var dataId = '';
          if (data && data.raw.id) {
            dataId = data.raw.id;
          } else {
            document.getElementById('articleSearchBox').getAttribute('data-id');
          }
          if (dataId && dataId.length > 0) {
              window.location.href = "https://www.eventbrite.com/support" + '/articleredirect?anum=' + dataId + "&lg=" + "en_US";
          } else {
            redirectToSearch();
          }
        }

        function redirectToSearch() {
          if (getSearchText() && getSearchText().length > 0) {
              window.location.href = "https://www.eventbrite.com/support" + "/searchsupport?q=" + encodeURIComponent(getSearchText()) + "&lg=" + "en_US";
          }
        }

        function getSearchText() {
            return $("#" + textBoxId).val();
        }

        //function setFocusOnLoad() {}
    </script>

