  <script>

    $(document).ready(function() {
      setFooterLinkHeaderStyles();
    });

    // Method to position search box
    function positionSearch() {
      var llwidth;
      if($('div#llrDesktop').is(':visible')) {
        llwidth = $('div#llrDesktop').width() + 10;
        if($('div#mobileHeaderSearchBox > span')) {
          $('div#mobileHeaderSearchBox > span').appendTo('div#desktopHeaderSearchBox');
        }
      }
      if($('div#llrMobile').is(':visible')) {
        llwidth = $('div#llrMobile a.js-header-locale-icon').width() + 10;
        if($('div#desktopHeaderSearchBox > span')) {
          $('div#desktopHeaderSearchBox > span').appendTo('div#mobileHeaderSearchBox');
        }
      }

      if(!$('div#llrDesktop').is(':visible') && !$('div#llrMobile').is(':visible')) {
        llwidth = 0;
        if($('div#mobileHeaderSearchBox > span')) {
          $('div#mobileHeaderSearchBox > span').appendTo('div#desktopHeaderSearchBox');
        }
      }

      $('div#desktopSearchDiv').parent().css('right', llwidth+'px').show();
    }

    function setFooterLinkHeaderStyles() {
      // use classes from styleguide
      // modifying with javascript because actual originally html-code with text is part of
      // content in database
      $(".js-helpcenter-footer-links").find("h3").removeClass().addClass("footer-text-heading-primary text-body-medium");
      $(".js-helpcenter-footer-links").find("a").removeClass().addClass("text-body-small");
      $(".js-helpcenter-footer-links").find("li").removeClass();
      $(".js-helpcenter-footer-copyright").find("p").removeClass().addClass("footer-copyright text-body-medium footer-links");
    }

    // Method to set the EB Support Logo Links
    function setLogoLinks() {
      var lgCode = getLanguageCode();
      var tldOrigin = getDocumentLocationInfo('origin');
      var mainPageLink ;
      // IE - fix
      if(tldOrigin.indexOf('https:///') !== -1){
          mainPageLink = tldOrigin + '/support?lg=' + lgCode;
        } else {
          mainPageLink = 'https://'+ tldOrigin + '/support?lg=' + lgCode;
        }
      $('a#eb-logo').attr('href', mainPageLink);
    }

    function getLanguageCode() {
      // If lg parameter in url exists, return as is∏
      var lgParam = getUrlVar('lg');
      if(lgParam.length > 0 && confirmLanguageCode(lgParam)){
        return lgParam;
      }

      // Else extrapolate language code from TLD
      lgParam = getLanguageCodeFromTLD(getTLD());
      if(lgParam != null && confirmLanguageCode(lgParam)){
        return lgParam;
      }

      // Else get browser language code
      lgParam = getBrowserLanguage();
      if(lgParam.length > 0 && confirmLanguageCode(lgParam)){
        return lgParam;
      }
      // Else use default
      lgParam = 'en_US'; // Default
      return lgParam;
    }

    /**
    ** Returns URL parameter value
    **/
    function getUrlVar(key){
      var result = new RegExp(key + "=([^&]*)", "i").exec(document.location.href);
      return result && unescape(result[1]) || "";
    }

    /**
    ** Returns Document.Location details for href, origin, hostname, host, pathname
    **/
    function getDocumentLocationInfo(type) {
      // Type: href , origin , hostname , host , pathname
      switch(type) {
        case 'href':
          return document.location.href;
          break;
        case 'origin':
            // IE fix:  use an absolute URL, Location origin Property not supported in IE
          var tldUrl = document.location.origin;
          if(typeof tldUrl === 'undefined'){
            tldUrl =document.location.hostname;
          }
          return tldUrl;
          break;
        case 'hostname':
          return document.location.hostname;
          break;
        case 'host':
          return document.location.host;
          break;
        case 'pathname':
          return document.location.pathname;
          break;
        default:
          return null;
      }
    }

    /**
    ** Attempts to get TLD from hostname
    **/
    function getTLD() {
      var hostname = document.location.hostname;
      var hostnameSplit = hostname.split('.');
      var hostnameSplitLength = hostnameSplit.length;
      var hostnameTLD = hostnameSplit.pop();
      while (hostnameSplit.length) {
        if (getLanguageCodeFromTLD(hostnameTLD)) {
          return hostnameTLD;
        }
        hostnameTLD = hostnameSplit.pop() + '.' + hostnameTLD;
      }
      return null;
    }

    /**
    ** Converts Browser's Language code to return Salesforce Ready Language Code
    **/
    function getBrowserLanguage() {
      var language = window.navigator.userLanguage || window.navigator.language;
      language = language.replace(/\-/g,"_");
      return language;
    }

    /**
    ** Returns Salesforce Ready Language Code based on Eventbrite's TLDs
    **/
    function getLanguageCodeFromTLD(tld) {
      switch(tld) {
        case 'com':
          return 'en_US';
          break;
        case 'ca':
          return 'en_CA';
          break;
        case 'co.nz':
          return 'en_NZ';
          break;
        case 'co.uk':
          return 'en_GB';
          break;
        case 'co.au':
          return 'en_AU';
          break;
        case 'hk':
          return 'en_HK';
          break;
        case 'IE':
          return 'en_IE';
          break;
        case 'sg':
          return 'en_SG';
          break;
        case 'de':
          return 'de';
          break;
        case 'es':
          return 'es';
          break;
        case 'fr':
          return 'fr';
          break;
        case 'it':
          return 'it';
          break;
        case 'nl':
          return 'nl_NL';
          break;
        case 'pt':
          return 'pt_PT';
          break;
        case 'com.br':
          return 'pt_BR';
          break;
        case 'com.ar':
          return 'es_AR';
          break;
        default:
          return null;
      }
    }

    /**
    ** Fixes Browser Language Codes for Salesforce
    **/
    function updateBrowserLanguageCode(lgCode) {
      switch(lgCode) {
        case 'fr_FR':
          return 'fr';
          break;
        case 'de_DE':
          return 'de';
          break;
        case 'es_ES':
          return 'es';
          break;
        case 'it_IT':
          return 'it';
          break;
        default:
          return lgCode;
      }
    }

    /**
    ** Validates SFDC Ready Language Code
    **/
    function confirmLanguageCode(lgCode) {
      if(lgCode == 'en_US' || lgCode == 'en_CA' || lgCode == 'fr_CA' || lgCode == 'en_NZ' ||
        lgCode == 'en_GB' || lgCode == 'en_AU' || lgCode == 'en_HK' || lgCode == 'en_IE' ||
        lgCode == 'en_SG' || lgCode == 'de' || lgCode == 'es' || lgCode == 'fr' ||
        lgCode == 'it' || lgCode == 'nl_NL' || lgCode == 'pt_PT' || lgCode == 'pt_BR' ||
        lgCode == 'es_AR')
      {
        return true;
      } else {
        return false;
      }
    }

    /**
     * Defines if the current organization is Production or Sandbox. Usually production usernames end
     * with "eventbrite.com" without any additional suffixes. this method uses dynamic VF page/js
     * code generation with VF expressions
     */
    function isSandbox() {
      var username = 'help_center@eventbrite.force.com';
      // from https://developer.mozilla.org/en-US/docs/Web/JavaScript/Reference/Global_Objects/String/endsWith
      if (!String.prototype.endsWith) {
        String.prototype.endsWith = function(searchString, position) {
          var subjectString = this.toString();
          if (typeof position !== 'number' || !isFinite(position)
              || Math.floor(position) !== position || position > subjectString.length) {
            position = subjectString.length;
          }
          position -= searchString.length;
          var lastIndex = subjectString.lastIndexOf(searchString, position);
          return lastIndex !== -1 && lastIndex === position;
        };
      }
      if ((username.endsWith('eventbrite.force.com') || username.endsWith('eventbrite.secure.force.com'))) {
        return false;
      } else {
        return true;
      }
    }
    // New Google Analytics Code - Added: 9/4/2015 - Universal Analystics js //
     /**
     * Creates a temporary global ga object and loads analytics.js.
     * Parameters o, a, and m are all used internally.  They could have been declared using 'var',
     * instead they are declared as parameters to save 4 bytes ('var ').
     *
     * @param {Window}      i The global context object.
     * @param {Document}    s The DOM document object.
     * @param {string}      o Must be 'script'.
     * @param {string}      g URL of the analytics.js script. Inherits protocol from page.
     * @param {string}      r Global name of analytics object.  Defaults to 'ga'.
     * @param {DOMElement?} a Async script tag.
     * @param {DOMElement?} m First script tag in document.
     */
    (function(i, s, o, g, r, a, m){
      i['GoogleAnalyticsObject'] = r; // Acts as a pointer to support renaming.

      // Creates an initial ga() function.  The queued commands will be executed once analytics.js loads.
      i[r] = i[r] || function() {
        (i[r].q = i[r].q || []).push(arguments)
      },

      // Sets the time (as an integer) this tag was executed.  Used for timing hits.
      i[r].l = 1 * new Date();

      // Insert the script tag asynchronously.  Inserts above current tag to prevent blocking in
      // addition to using the async attribute.
      a = s.createElement(o),
      m = s.getElementsByTagName(o)[0];
      a.async = 1;
      a.src = g;
