    // JS Function to fix the alignment issue regarding the Top Promotional Component
    function setHeightTopPromotionalComponent() {
      var tpcTextHeight = 0;

      // Set height to auto
      $('div.helpcenter-card p').css('height', 'auto' );

      // Determine max Height
      $('div.helpcenter-card p').each(function(){
        var currentHeight = $(this).height();
        tpcTextHeight = currentHeight > tpcTextHeight ? currentHeight : tpcTextHeight;
      });

      // Apply Max Height
      $('div.helpcenter-card p').css('height', tpcTextHeight+'px' );
    }

    // JS Function to fix the alignment issue regarding the DataCategory Component
    function setHeightDataCategoryComponent() {
      var dccTextHeight = 0;

      // Set height to auto
      $('ul.helpcenter-topic-list li').css('height', 'auto');

      // Determine max Height
      $('ul.helpcenter-topic-list li').each(function(){
        var currentHeight = $(this).height();
        dccTextHeight = currentHeight > dccTextHeight ? currentHeight : dccTextHeight;
      });

      // Apply Max Height
      $('ul.helpcenter-topic-list li').css('height', dccTextHeight+'px');
    }
